Scripts.OnClick:
 if ( IsShiftKeyDown() ) then 
					local ItemName, ItemLink = GetItemInfo("item:"..GS_Data[GetRealmName()].Players[GS_DisplayPlayer].Equip[self:GetID()]); ChatFrameEditBox:Insert(ItemLink); 
					end

Scripts.OnEnter:
local Name = tonumber(string.sub(self:GetName(), 9)) local count = 0 GameTooltip:SetOwner(self, "ANCHOR_Right") GameTooltip:ClearLines(); GameTooltip:AddLine(GS_InstanceOrder[Name]); --local StatString = GS_Data[GetRealmName()].Players[GS_DisplayPlayer].StatString local StatString = GS_Data[GetRealmName()]["CurrentPlayer"]["EXP"] if not StatString then return; end local TempTable = {} --for i,v in pairs(GS_AchInfo[GS_InstanceOrder[Name]]) do -- local InsertionValue = { ["i"] = i, ["v"] = v } -- table.insert(TempTable, InsertionValue ) --end --table.sort(TempTable, function(a, b) return a["i"] < b["i"] end )
for i,v in pairs(GS_AchInfo[GS_InstanceOrder[Name]]) do --if not (GS_BackString[i]) then print(i,v) end count = tonumber(string.sub(StatString, GS_BackString[i],GS_BackString[i])) if not count then count = 0; end GameTooltip:AddDoubleLine(count.." kills ("..(count/0.05).."%)",v,1 - (count/5),0 + (count/5),0,1,1,1)
end GameTooltip:AddLine(" ") if ( GS_Settings["ShowHelp"] == 1 ) then GameTooltip:AddLine("Remember, this player could be an alt and still know the fights.", 1, 1, 1, 1, true); end GameTooltip:AddLine("*Note: '5' means 5 or more kills") GameTooltip:Show()

